sql.where:
and `timestmp` > #{afterTime}
and #{beforeTime} > `timestmp`
and `logger_name` = #{logger_name} 
and `formatted_message` like CONCAT('%',#{formatted_message},'%')
and `level_string` = #{level_string}
and `logger_name` like CONCAT(#{module},'%')
and `level_string` != 'DEBUG'

and lep.`mapped_key` = 'SESSION_ID' and lep.`mapped_value` = #{session_id}
and lep.`mapped_key` = 'LOGIN' and lep.`mapped_value` like CONCAT('%',#{login},'%')
and lep.`mapped_key` = 'MODEL' and lep.`mapped_value` = #{model}
and lep.`mapped_key` = 'OBJECT_ID' and lep.`mapped_value` = #{objectId}
and lep.`mapped_key` = 'CATEGORY' and lep.`mapped_value` = #{category.value}

mapper.select:
select distinct le.`event_id` as id, `timestmp`, `caller_class`, `logger_name`, `formatted_message`, `level_string`
from `logging_event` as le
left join `logging_event_property` as lep on (le.event_id = lep.event_id)
left join `logging_event_property` as lep1 on (le.event_id = lep1.event_id and lep1.`mapped_key` = #{sortProperty})



        order by `timestmp` ${asc}


        order by `logger_name` ${asc}


        order by `formatted_message` ${asc}


        order by `level_string` ${asc}


        order by SUBSTRING_INDEX(`logger_name`, '.', 4) ${asc}


        order by lep1.`mapped_value` ${asc}


limit #{first}, #{count}
